# 获取中文本地化
def get_zh_cn_localization():
    return ZH_CN_LOCALIZATION

# 获取特定的本地化文本
def t(key_path: str, **kwargs):
    """
    获取本地化文本
    参数:
        key_path: 使用点号分隔的路径，如 "ui.analyze" 或 "modules.deep_research"
        **kwargs: 用于格式化文本的参数
    返回:
        本地化的文本字符串
    """
    keys = key_path.split(".")
    value = ZH_CN_LOCALIZATION

    for key in keys:
        if isinstance(value, dict):
            value = value.get(key, f"[{key_path}]")
        else:
            return f"[{key_path}]"

    if isinstance(value, str):
        return value.format(**kwargs) if kwargs else value
    return str(value)
